// using jQuery
function getCookie(name) {
    var cookieValue = null;
    if (document.cookie && document.cookie != '') {
        var cookies = document.cookie.split(';');
        for (var i = 0; i < cookies.length; i++) {
            var cookie = jQuery.trim(cookies[i]);
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) == (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

var csrftoken = getCookie('X-CSRFToken');

function csrfSafeMethod(method) {
    // these HTTP methods do not require CSRF protection
    return (/^(GET|HEAD|OPTIONS|TRACE)$/.test(method));
}

$.ajaxSetup({
    crossDomain: false, // obviates need for sameOrigin test
    beforeSend: function (xhr, settings) {
        if (!csrfSafeMethod(settings.type)) {
            xhr.setRequestHeader("X-CSRFToken", csrftoken);
        }
    }
});

(function ($) {

    "use strict";

    /**
     *  
     */
    var fileapi = function () {
        return fileapi.init.apply(this, arguments);
    };

    /**
     *  
     */
    $.extend(fileapi, {
        /**
         *   
         */
        options: {
            option: 500,
            CKEditorFuncNum: undefined,
            CKEditor: undefined,
            startPath: undefined,
            listUrl: undefined,
            uploadUrl: undefined,
            filemanSelector: '.file-manager',
            manageSelector: '.manage',
            messagesSelector: '.messages',
            messagesTimeout: 10000,
            csrfName: undefined,
            csrf: undefined,
            deletePrevention: 'Do you really want to delete the file?'
        },
        /**
         * ,    
         */
        element: undefined,
        $element: undefined,
        currentPath: undefined,
        manage: {},
        /**
         * 
         * @param element
         * @param options
         */
        init: function (element, options) {
            if (element === undefined) return;

            this.element = element;
            this.$element = $(element);
            this.options = $.extend(this.options, options);

            this.currentPath = this.options.startPath;

            this.initManagement();
            this.initUploader();
            this.bind();

            return this;
        },
        initManagement: function () {
            var me = this;

            me.manage['manage'] = $(this.options.manageSelector);
            me.manage['create_folder'] = {
                'button': me.manage.manage.find('.create-folder-button'),
                'input': me.manage.manage.find('.create-folder-input')
            };
            me.manage.create_folder.input.hide();
        },
        initUploader: function () {
            var me = this;

            var query = {
                'path': this.currentPath
            };
            query[this.options.csrfName] = this.options.csrf;

            var flow = new Flow({
                target: this.options.uploadUrl,
                query: query
            });

            flow.assignBrowse(document.getElementById('select'));
            flow.assignDrop(document.getElementById('zone'));

            flow.on('fileAdded', function(file, event){});

            flow.on('filesSubmitted', function(){
                flow.opts['query']['path'] = me.currentPath;
                flow.upload();
            });

            flow.on('uploadStart', function(){
                $('#progress_bar').css({
                    'width': 0
                });
                me.setUploading();
            });

            flow.on('progress', function(){
                var width = flow.progress() * 100 + '%';
                $('#progress_bar').css({
                    'width': width
                });
            });

            flow.on('complete', function(){
                $('#progress_bar').css({
                    'width': '100%'
                });
                me.unsetUploading();
                me.updateList();
            });
        },
        /**
         * "" 
         */
        bind: function () {
            var me = this;

            this.$element.on('click', '.files .file-check', function (e) {
                if (!$(e.target).is('input')){
                    e.preventDefault();
                    $(this).find('input').trigger('click');
                    return false;
                }
            });

            this.$element.on('click', '.files .file-name', function (e) {
                e.preventDefault();
                $(this).find('a').click();
                return false;
            });

            this.$element.on('click', '.files .file .file-link', function (e) {
                e.preventDefault();
                me.openFile($(this).data('url'));
                return false;
            });

            this.$element.on('click', '.files .dir .file-link', function (e) {
                e.preventDefault();
                me.openFolder($(this).data('path'));
                return false;
            });

            this.$element.on('click', '.files .delete-link', function (e) {
                e.preventDefault();
                if (confirm(me.options.deletePrevention)) {
                    me.deleteFile($(this).data('path'));
                }
                return false;
            });

            this.$element.on('click', '.create-folder-button', function (e) {
                e.preventDefault();

                if (!me.manage.create_folder.input.is(':visible')) {
                    me.manage.create_folder.input.show(0);
                    me.manage.create_folder.input.focus();
                } else {
                    me.createFolder(me.manage.create_folder.input.val());
                }

                return false;
            });

            this.$element.on('click', '.remove-selected', function(e) {
                e.preventDefault();
                if (confirm(me.options.deletePrevention)) {
                    me.deleteAll();
                }
                return false;
            });

            var collection = $();
            $(document).on('dragenter', function (e) {
                me.showDropInfo();
                collection = collection.add(e.target);
            }).on('dragleave',function (e) {
                collection = collection.not(e.target);
                if (!collection.length) {
                    me.hideDropInfo();
                }
            }).on('drop', function () {
                collection = $();
                me.hideDropInfo();
            });
        },
        showDropInfo: function() {
            var fileman = this.$element;
            if (!fileman.hasClass('frop'))
                fileman.addClass('drop');
        },
        hideDropInfo: function() {
            this.$element.removeClass('drop');
        },
        setUploading: function() {
            this.$element.addClass('uploading');
        },
        unsetUploading: function() {
            this.$element.removeClass('uploading');
        },
        openFile: function (url) {
            var me = this;
            $(document).trigger('fileman.opened',[url]);
        },
        openFolder: function (path) {
            var me = this;
            me.updateList(path);
        },
        createFolder: function (name) {
            var me = this;
            me.api('make', {'name': name}, function (data) {
                if (data.statement == 'success') {
                    me.manage.create_folder.input.hide();
                    me.manage.create_folder.input.val('');
                    me.updateList();
                }
            });
        },
        deleteAll: function() {
            var me = this;
            var files = [];
            $('input.delete-checker:checked').each(function(){
                files.push($(this).val());
            });
            me.api('deleteAll', {'files': files}, function (data) {
                $('input.delete-checker').removeAttr('checked');
                me.updateList();
            });
        },
        deleteFile: function (name) {
            var me = this;
            me.api('delete', {'name': name}, function (data) {
                if (data.statement == 'success') {
                    me.updateList();
                }
            });
        },
        api: function (action, sendData, callback) {
            var me = this;
            sendData = sendData || {};
            if (!sendData.path) {
                sendData.path = me.currentPath
            }
            sendData['action'] = action;
            sendData[me.options.csrfName] = me.options.csrf;
            $.ajax({
                'type': 'post',
                'url': me.options.apiUrl,
                'data': sendData,
                'dataType': 'json',
                'success': function (data) {
                    if (data.statement && data.message) {
                        if (data.statement == 'error') {
                            me.error(data.message);
//                        }else{
//                            me.message(data.message);
                        }
                    }
                    if (callback) {
                        callback(data);
                    }
                }
            })
        },
        message: function (message, type) {
            var me = this;
            type = type || 'message';

            var $notification = $('<div/>').addClass('notification').addClass(type).html(message);
            var $messages = $(me.options.messagesSelector);
            $messages.append($notification);

            setTimeout(function () {
                $notification.remove();
            }, me.options.messagesTimeout)
        },
        error: function (message) {
            this.message(message, 'error');
        },
        updateList: function (path) {
            var me = this;
            path = path || me.currentPath;
            var sendData = {
                'path': path
            };
            $.ajax({
                'url': me.options.listUrl,
                'data': sendData,
                'dataType': 'html',
                'success': function (data) {
                    var wrapped_data = $('<div/>').append(data);
                    $(me.element).find('.files').replaceWith(wrapped_data.find('.files'));
                    me.currentPath = path;
                }
            });
        }
    });

    /**
     *     jQuery
     */
    return $.fn.fileapi = function (options) {
        return fileapi.init(this, options);
    };

})($);

(function ($) {

    "use strict";

    var filesField = {
        options: {
            listId: undefined,

            uploadUrl: undefined,
            sortUrl: undefined,
            deleteUrl: undefined,

            flowData: {},
            sortData: {},
            deleteData: {}
        },
        element: undefined,
        init: function (element, options) {
            if (element === undefined) return;

            this.element = element;
            this.options = $.extend(this.options, options);

            this.bind();
            this.initUploader();
            this.initList();

            return this;
        },
        bind: function()
        {
            var me = this;
            $(document).on('click', '#' + me.options.listId + ' .remove', function(e){
                e.preventDefault();
                var $item = $(this).closest('li');
                if ($item.data('pk')) {
                    me.remove($item.data('pk'));
                }
                return false;
            });
        },
        initUploader: function () {
            var me = this;

            var flow = new Flow({
                target: me.options.uploadUrl,
                query: me.options.flowData
            });

            flow.assignBrowse(this.element);
            flow.assignDrop(this.element);

            flow.on('filesSubmitted', function(){
                flow.upload();
            });

            flow.on('uploadStart', function(){
                $(me.element).find('.progress_bar').css({
                    'width': 0
                });
            });

            flow.on('progress', function(){
                var width = flow.progress() * 100 + '%';
                $(me.element).find('.progress_bar').css({
                    'width': width
                });
            });

            flow.on('complete', function(){
                $(me.element).find('.progress_bar').css({
                    'width': '100%'
                });
                me.updateList();
            });
        },
        updateList: function () {
            var me = this;
            $.ajax({
                'url': window.location.href,
                'dataType': 'html',
                'success': function (data) {
                    var wrapped_data = $('<div/>').append(data);
                    console.log(wrapped_data);
                    console.log(me.options.listId);
                    $('#' + me.options.listId).replaceWith(wrapped_data.find('#' + me.options.listId));
                    me.initList();
                }
            });
        },
        initList: function() {
            var me = this;
            $("#" + this.options.listId).dragsort({
                dragSelector: "li",
                placeHolderTemplate: "<li class='empty'></li>",
                dragEnd: function() {
                    me.sort();
                }
            });
        },
        sort: function() {
            var pk = [];
            var me = this;
            var data = me.options.sortData;

            $("#" + this.options.listId).find('li').each(function(){
                if ($(this).data('pk')) {
                    pk.push($(this).data('pk'));
                }
            });

            data['pk'] = pk;

            $.ajax({
                'type': 'post',
                'url': me.options.sortUrl,
                'data': data
            });
        },
        remove: function(pk) {
            var me = this;
            var data = me.options.deleteData;
            data['pk'] = pk;
            $.ajax({
                'type': 'post',
                'url': me.options.deleteUrl,
                'data': data,
                'success': function(){
                    $('#' + me.options.listId).find('[data-pk="'+pk+'"]').fadeOut(300, function(){
                        $(this).remove();
                    });
                }
            });
        }
    };

    /**
     *     jQuery
     */
    return $.fn.filesField = function (options) {
        return $.extend(true, {}, filesField).init(this, options);
    };

})($);
